import React, { Component } from 'react';
import ReportService from '../services/ReportService';

class CreateReportComponent extends Component {
    constructor(props){
        super(props)

        this.state={
            reportName:"",
            reportType:"",
            adminId:"",
        }
       this.changeAdminIdHandler =this.changeAdminIdHandler.bind(this);
       this.changeReportNameHandler =this.changeReportNameHandler.bind(this);
       this.changeReportTypeHandler =this.changeReportTypeHandler.bind(this);
       this.saveReport = this.saveReport.bind(this);
    }

    saveReport=(e)=>{
        e.preventDefault();
        let report ={adminId: this.state.adminId,reportName: this.state.reportName,reportType: this.state.reportType};
        console.log("report =>" + JSON.stringify(report));

        ReportService.createReport(report).then(res=>{
            this.props.history.push('/reports');
        });
    }

    changeAdminIdHandler =(event)=>{
        this.setState({adminId: event.target.value});
    }
    changeReportNameHandler =(event)=>{
        this.setState({reportName: event.target.value});
    }
    changeReportTypeHandler =(event)=>{
        this.setState({reportType: event.target.value});
    }
    cancel(){
        this.props.history.push('/reports');
    }
    render() {
        return (
            <div>
                <br></br>
                <div className="container">
                    <div className ="row">
                        <div className="card col-md-6 offset-md-3 offset-md-3">
                            <br></br>
                            <h3 className="text-center">Add Report</h3>
                            <div className="card-body">
                                <form>
                                    <div  style={{marginBottom:"10px"}}className="form-group ">
                                        <label>Report Id</label>
                                        <input placeholder="Report Id" name="adminId" className="form-control"
                                        value={this.adminId} onChange={this.changeAdminIdHandler} />
                                    </div>
                                    
                                    <div style={{marginBottom:"10px"}} className="form-group">
                                        <label>Report Name</label>
                                        <input placeholder="Report Name" name="reportName" className="form-control"
                                        value={this.reportName} onChange={this.changeReportNameHandler} />
                                    </div>
                                    <div style={{marginBottom:"10px"}} className="form-group">
                                        <label>Report Type</label>
                                        <input placeholder="Report Type" name="reportType" className="form-control"
                                        value={this.reportType} onChange={this.changeReportTypeHandler} />
                                    </div>
                                    <br></br>
                                    <button className="btn btn-success" onClick={this.saveReport}>Save</button>
                                    <button className="btn btn-danger" onClick={this.cancel.bind(this)} style={{marginLeft: "10px"}}>Cancel</button>
                                </form>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default CreateReportComponent;
