import axios from "axios";
import React, { Component } from "react";
import ReportService from "../services/ReportService";

class CreateReportComponent extends Component {
  constructor(props) {
    super(props);

    this.state = {
      reportId: this.props.match.params.reportId,
      reportName: "",
      agentName: "",
      address: "",
      contact: "",
    };
    this.changeReportNameHandler = this.changeReportNameHandler.bind(this);
    this.changeAgentNameHandler = this.changeAgentNameHandler.bind(this);
    this.changeAddressHandler = this.changeAddressHandler.bind(this);
    this.changeContactHandler = this.changeContactHandler.bind(this);
    this.saveOrUpdateReport = this.saveOrUpdateReport.bind(this);
  }

  componentDidMount() {
    if (this.state.reportId === "_add") {
      axios.post("report/add", {
        reportName: this.state.reportName,
        agentName: this.state.agentName,
        address: this.state.address,
        contact: this.state.contact,
      });
    } else {
      ReportService.getReportById(this.state.reportId).then((res) => {
        let travel = res.data;
        this.setState({
          reportName: travel.reportName,
          agentName: travel.agentName,
          address: travel.address,
          contact: travel.contact,
        });
      });
    }
  }
  saveOrUpdateReport = (e) => {
    e.preventDefault();
    let travel = {
      reportName: this.state.reportName,
      agentName: this.state.agentName,
      address: this.state.address,
      contact: this.state.contact,
    };
    console.log("travel => " + JSON.stringify(travel));

    if (this.state.id === "_add") {
      ReportService.updateReport(travel, this.state.reportId).then((res) => {
        this.props.history.push("/report");
      });
    } else {
      ReportService.createReport(travel).then((res) => {
        this.props.history.push("/report/_add");
      });
    }
  };

  changeReportNameHandler = (event) => {
    this.setState({ reportName: event.target.value });
  };

  changeAgentNameHandler = (event) => {
    this.setState({ agentName: event.target.value });
  };

  changeAddressHandler = (event) => {
    this.setState({ address: event.target.value });
  };

  changeContactHandler = (event) => {
    this.setState({ contact: event.target.value });
  };

  cancel() {
    this.props.history.push("/report");
  }

  getTitle() {
    if (this.state.reportId === "_add") {
      return <h3 className="text-center">Add Report</h3>;
    } else {
      return <h3 className="text-center">Update Report</h3>;
    }
  }
  render() {
    return (
      <div>
        <br></br>
        <div className="container">
          <div className="row">
            <div className="card col-md-6 offset-md-3 offset-md-3">
              {this.getTitle()}
              <div className="card-body">
                <form>
                  <div className="form-group">
                    <label> Report Name: </label>
                    <input
                      placeholder="Report Name"
                      name="reportName"
                      className="form-control"
                      value={this.state.reportName}
                      onChange={this.changeReportNameHandler}
                    />
                  </div>
                  <div className="form-group">
                    <label> Agent Name: </label>
                    <input
                      placeholder="Agent Name"
                      name="agentName"
                      className="form-control"
                      value={this.state.agentName}
                      onChange={this.changeAgentNameHandler}
                    />
                  </div>
                  <div className="form-group">
                    <label> Address: </label>
                    <input
                      placeholder="Address"
                      name="address"
                      className="form-control"
                      value={this.state.address}
                      onChange={this.changeAddressHandler}
                    />
                  </div>
                  <div className="form-group">
                    <label> Contact: </label>
                    <input
                      placeholder="Contact"
                      name="contact"
                      className="form-control"
                      value={this.state.contact}
                      onChange={this.changeContactHandler}
                    />
                  </div>

                  <button
                    className="btn btn-success"
                    onClick={this.saveOrUpdateReport}
                  >
                    Save
                  </button>
                  <button
                    className="btn btn-danger"
                    onClick={this.cancel.bind(this)}
                    style={{ marginLeft: "10px" }}
                  >
                    Cancel
                  </button>
                </form>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default CreateReportComponent;
